import 'package:flutter/material.dart';import '../flutter_flow/flutter_flow_theme.dart';class CustomTextFormField extends StatelessWidget {  final String label;  final String Function(String? val)? validator;  final TextEditingController? controller;  const CustomTextFormField(      {Key? key,      required this.label,      required this.validator,      required this.controller})      : super(key: key);  @override  Widget build(BuildContext context) {    return TextFormField(      validator: validator,      controller: controller,      style: TextStyle(        fontSize: 14,      ),      onChanged: (value) {},      decoration: InputDecoration(        focusColor: Colors.white,        prefixIcon: Icon(Icons.input),        border: OutlineInputBorder(          borderSide: BorderSide(              color: FlutterFlowTheme.of(context).primary, width: 1.0),          borderRadius: BorderRadius.circular(10.0),        ),        focusedBorder: OutlineInputBorder(          borderSide: const BorderSide(color: Colors.green, width: 1.0),          borderRadius: BorderRadius.circular(10.0),        ),        labelText: label,      ),    );  }}