import 'package:flutter/material.dart';import 'package:jem_job_app/backend/backend.dart';class UserProvider extends ChangeNotifier {  UsersRecord currentEmployeeRecord = UsersRecord();  updateUserRecord(UsersRecord usersRecord) {    currentEmployeeRecord = usersRecord;    notifyListeners();  }  GeoPoint? newLocation = GeoPoint(0, 0);  updateLocation(GeoPoint geoPoint) {    newLocation = geoPoint;    notifyListeners();  }}